import React, { useEffect, useState } from 'react'
import { useLocation } from 'react-router-dom'
import SensorChart from '../components/SensorChart'
import SensorCard from '../components/SensorCard'
import axios from 'axios'

function DashBoard() {
    const location=useLocation()
    const [data,setdata]=useState([])
    const [sensorData, setSensorData] = useState([]);
    const [selectedSensor, setSelectedSensor] = useState(null);
    const [chartData, setChartData] = useState([]);
    let sensored=[]
    sensored.p
    console.log(sensorData);

    useEffect(() => {
      const fetchData = async () => {
          try {
              const response = await axios.get('http://localhost:5000/api/v1/sensors/simulate');
              setSensorData(response.data);
          } catch (error) {
              console.error('Error fetching sensor data:', error);
          }
      };

      fetchData();
  }, []);


  const handleCardClick = async (sensorType) => {
    setSelectedSensor(sensorType);
    try {
        const response = await axios.get(`http://localhost:5000/api/sensor-data/${sensorType}`);
        setChartData(response.data);
    } catch (error) {
        console.error(`Error fetching data for ${sensorType}:`, error);
    }
};

